import './roomTopbar.css'
import { useHistory } from "react-router";
import { AuthContext } from "../../context/AuthContext";
import { useContext } from "react";
import axios from 'axios'

export default function RoomTopbar({room}) {
    const { user } = useContext(AuthContext)
    const history = useHistory();

    const handleLeave = async (e) => {
        e.preventDefault();

        // if host leaves delete room, else just leave room
        if (user._id === room.hostID) {
            // delete room messages first
            try {
                await axios.delete(`/messages/${room._id}`);
            } catch(err) {
                console.log(err)
            }

            // delete room
            try {
                await axios.delete(`/rooms/${user._id}`);
                history.push(`/login`)
            } catch(err) {
                console.log(err)
            }


        } else {
            try {
                await axios.put(`/rooms/${room.code}/leave`, { userID: user._id});
                history.push(`/main`)
            } catch(err) {
                console.log(err)
            }
        }
    }

    return (
        <div className='roomTopbar'>
            <button className="roomBackButton" onClick={handleLeave}>
                Leave Room
            </button>
        </div>
    )
}
