import Message from '../message/Message'
import './chat.css'
import { useContext, useRef, useState, useEffect } from 'react';
import { AuthContext } from '../../context/AuthContext'
import { io } from 'socket.io-client'
import axios from 'axios'

export default function Chat({room}) {
  const [newMessage, setNewMessage] = useState("");
  const { user } = useContext(AuthContext)
  const scrollRef = useRef()
  const [messages, setMessages] = useState([]);
  const socket = useRef()
  const [arrivalMessage, setArrivalMessage] = useState(null);

  // initialize socket connection
  useEffect(() => {
    socket.current = io("ws://localhost:8900");

    // look for incoming messages
    socket.current.on("newMessage", (data) => {
      setArrivalMessage({
        senderID: data.senderID,
        text: data.text,
        createdAt: Date.now(),
        roomID: room._id,
        senderProfilePicture: user.profilePicture
      });
    });
  }, []);

  // join socket room
  useEffect(() => {
    room && socket.current.emit("joinRoom", room.code)
  }, [room])

  // new message from socket
  useEffect(() => {
    arrivalMessage && setMessages((prev) => [...prev, arrivalMessage]);
  }, [arrivalMessage]);

  // get room messages
  useEffect(() => {
    const getMessages = async () => {
      try {
        const res = await axios.get(`/messages/${room._id}`);
        setMessages(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    getMessages();
  }, [room]);

  const handleSend = async (e) => {
    e.preventDefault();

    const message = {
      senderID: user._id,
      text: newMessage,
      roomID: room._id,
      senderProfilePicture: user.profilePicture
    };

    // send message to socket
    socket.current.emit("sendMessage", { senderID: user._id, roomName: room.code, text: newMessage})

    try {
      const res = await axios.post("/messages", message);
      setMessages([...messages, res.data]);
      setNewMessage("");
    } catch (err) {
      console.log(err);
    }
  };

  // scroll to bottom of messages
  useEffect(() => {
    scrollRef.current?.scrollIntoView({behavior: 'smooth'})
  }, [messages])

  return (
    <>
    <div className="chatTop">
      {messages.map((m) => (
        <div ref={scrollRef} key={m._id}>
          <Message message={m} own={m.senderID === user._id} />
        </div>
      ))}
    </div>

    <div className="chatBottom">
      <textarea
        className="chatMessageInput"
        placeholder="write something..."
        onChange={(e) => setNewMessage(e.target.value)}
        value={newMessage}
      >
      </textarea>
      <button className="chatSendButton" onClick={handleSend}>
        Send
      </button>
    </div>
    </>
  )
}
