const https = require('https')
const BASE_URL = 'https://www.googleapis.com/youtube/v3/search?maxResults=1&path=snippet&type=video&key=' + 
`${process.env.REACT_APP_YOUTUBE_API_KEY}&q=`

export const searchYouTubeVideo = async (query) => {
    https.get(BASE_URL + query, (response) => {
        data = ''

        response.on('data', (chunk) => {
            data += chunk
        })

        response.on('end', () => {
            return data
        })
    })
};
