import './room.css'
import VideoPlayer from '../../components/videoPlayer/VideoPlayer'
import Chat from '../../components/chat/Chat'
import Header from '../../components/header/Header'
import { useParams, useHistory } from "react-router";
import { useState, useEffect, useContext } from 'react';
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";
import { Search } from '@material-ui/icons'

export default function Room() {
    const [room, setRoom] = useState({})
    const roomCode = useParams().roomCode;
    const { user } = useContext(AuthContext)
    const history = useHistory();
    const [query, setQuery] = useState("")
    const [videoID, setVideoID] = useState(null)

    const handleLeave = async (e) => {
        e.preventDefault();

        // if host leaves delete room
        if (user._id === room.hostID) {
            // delete room messages first
            try {
                await axios.delete(`/messages/${room._id}`);
            } catch(err) {
                console.log(err)
            }

            // delete room
            try {
                await axios.delete(`/rooms/${user._id}`);
                history.push(`/login`)
            } catch(err) {
                console.log(err)
            }
        }
        else {
            history.push(`/login`)
        }
    }

    const handleSearch = async (e) => {
        e.preventDefault();

        setVideoID(query.split('v=')[1].split('_')[0])

        // setVideoID(YOUTUBE_VIDEO_ID_REGEX.exec(query))
        // console.log(videoID)

        // try {
        //     const res1 = await axios.get(`/youtube`, { query: query });
        //     const res2 = await axios.get(`/youtube`, { query: res.data.items[0].id.videoId});
        //     console.log()
        // } catch(err) {
        //     console.log(err)
        // }
    }

    // get room object
    useEffect(() => {
        const fetchRoom = async () => {
            const res = await axios.get(`/rooms/${roomCode}`);
            setRoom(res.data);
        };
        fetchRoom();
    }, [roomCode]);

    // initialize socket connection
  useEffect(() => {
    socket.current = io("ws://localhost:8900");

    // look for incoming messages
    socket.current.on("newMessage", (data) => {
      setArrivalMessage({
        senderID: data.senderID,
        text: data.text,
        createdAt: Date.now(),
        roomID: room._id,
        senderProfilePicture: data.senderProfilePicture
      });
    });
  }, [room]);

  // join socket room
  useEffect(() => {
    room && socket.current.emit("joinRoom", room.code)
  }, [room])

  // get room messages
  useEffect(() => {
    const getMessages = async () => {
      try {
        const res = await axios.get(`/messages/${room._id}`);
        setMessages(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    getMessages();
  }, [room]);

  // new message from socket
  useEffect(() => {
    arrivalMessage && setMessages((prev) => [...prev, arrivalMessage]);
  }, [arrivalMessage]);

  const handleSend = async (e) => {
    e.preventDefault();

    const message = {
      senderID: user._id,
      text: newMessage,
      roomID: room._id,
      senderProfilePicture: user.profilePicture
    };

    // send message to socket
    socket.current.emit("sendMessage", { senderID: user._id, roomName: room.code, text: newMessage, senderProfilePicture: user.profilePicture})

    try {
      const res = await axios.post("/messages", message);
      setMessages([...messages, res.data]);
      setNewMessage("");
    } catch (err) {
      console.log(err);
    }
  };

  // scroll to bottom of messages
  useEffect(() => {
    scrollRef.current?.scrollIntoView({behavior: 'smooth'})
  }, [messages])

    return (
        <>
        <Header/>
        {room ? (
            <>
            <div className='roomTopbar'>
                <div className="roomTopbarLeft">
                    <button className="roomBackButton" onClick={(handleLeave)}>
                        Leave Room
                    </button>
                </div>

                <div className="roomTopbarCenter">
                    <div className="roomSearchbar">
                        <Search className="searchIcon"/>
                        <input
                          placeholder="Enter YouTube video link here"
                          className="searchInput"
                          onChange={(e) => setQuery(e.target.value)}
                        />
                    </div>
                </div>

                <div className="roomTopbarRight">
                    <button className="searchSubmit" onClick={(handleSearch)}>
                        Search
                    </button>
                </div>
            </div>

            <div className='room'>
                <div className="roomVideo">
                    <div className="roomVideoWrapper">
                        {videoID ? (
                            <div className="videoPlayer">
                                <iframe
                                  width='1070'
                                  height='562'
                                  src={`http://youtube.com/embed/${videoID}`} 
                                  frameBorder='0' 
                                  allowFullScreen>
                                </iframe>
                            </div>
                        ) : (
                            <h1 className="noVideoMessage">Enter a YouTube video link</h1>
                        )}
                    </div>
                </div>

                <div className="roomChat">
                    <div className="roomChatWrapper">
                        <div className="chatTop">
                            {messages.map((m) => (
                                <div ref={scrollRef} key={m._id}>
                                    <Message message={m} own={m.senderID === user._id} />
                                </div>
                            ))}
                        </div>

                        <div className="chatBottom">
                            <textarea
                              className="chatMessageInput"
                              placeholder="write something..."
                              onChange={(e) => setNewMessage(e.target.value)}
                              value={newMessage}
                            >
                            </textarea>
                            <button className="chatSendButton" onClick={handleSend}>
                                Send
                            </button>
                        </div>
                    </div>
                </div>
            </div>
            </>
        ) : (
            <>Room does not exist</>
        )}
        </>
    )
}
