const https = require('https')
const BASE_URL = 'https://www.googleapis.com/youtube/v3/search?maxResults=1&path=snippet&type=video&key=AIzaSyBYHFYP_JZIhOEFszi6j9UyZI2UezLN8ow&q='

export const searchYouTubeVideo = async (query) => {
    https.get(BASE_URL + query, (response) => {
        let data = ''

        response.on('data', (chunk) => {
            data += chunk
        })

        response.on('end', () => {
            let result = JSON.parse(data)
            return result
        })
    })
    .on('error', (error) => {
        console.log(error)
    })
};
