import './join.css'
import { useContext, useState } from 'react';
import { TextField } from '@material-ui/core'
import { Link } from "react-router-dom";
import { AuthContext } from '../../context/AuthContext'
import { useHistory } from "react-router";
import axios from 'axios'

export default function Join() {
    const [code, setCode] = useState("");
    const { user } = useContext(AuthContext)
    const history = useHistory();

    const handleJoin = async (e) => {
        try {
            const res = await axios.put(`/rooms/${code}/join`, { userID: user._id });
            history.push(`/room/${code}`)
        } catch(err) {
            console.log(err)
        }
    }

    return (
        <div className="join">
            <div className="joinWrapper">
                <div className="joinTop">
                    <span className="joinLogo">
                        Join a Room
                    </span>
                </div>

                <div className="joinBottom">
                    <div className="joinOptionsBox">
                        <TextField
                          label='Code'
                          placeholder='Enter a Room Code'
                          variant='outlined'
                          onChange={(e) => setCode(e.target.value)}
                          value={code}
                        />

                        <button className="joinJoinButton" onClick={handleJoin}>
                            Join Room
                        </button>

                        <Link to='/'>
                            <button className="joinBackButton" onClick={handleJoin}>
                                Back
                            </button>
                        </Link>
                    </div>
                </div>
            </div>
        </div>
    )
}
